targetfile="aws-schema.graphql"

echo "{"
prefix=""
while IFS=, read -r cmd object
do
    echo "$prefix\"$object\":"
    aws $cmd --max-items 2 > $object
    cat $object
    prefix=","
done < aws.csv

echo "}"

# | npx @walmartlabs/json-to-simple-graphql-schema| sed -e "s/AutogeneratedMainType/AWS/g"
